mapper.select:
select * from
(select rownum rn,a.userid,a.realname,a.sex,a.address,a.idnumber,b.username,b.telephone,b.state,c.item academic,d.item income,e.item marriage from tb_realname a
 join tb_user b on a.userid=b.userid
 join tb_systemcategoryitem c on a.academic =c.id
join  tb_systemcategoryitem d on a.income = d.id
join tb_systemcategoryitem e on a.marriage=e.id
 where rownum < #{end}
) d where d.rn > #{start}

    and realname like '%'||#{realname}||'%'
