mapper.select:
select * from (
select rownum rn,to_char(a.AUDITTIME,'yyyy-MM-dd') as AUDITTIME,a.AUDITREMARKS,a.id,a.realname,a.address,a.applicationtime,a.idnumber,a.sex,a.positive,f.item auditorid,a.opposite,b.item housed,c.item academic,d.item marriage,e.item income from tb_realname a
left join tb_systemcategoryitem b on a.housed=b.id
left join tb_systemcategoryitem c on a.academic=c.id
left join tb_systemcategoryitem d on a.marriage=d.id
left join tb_systemcategoryitem e on a.income=e.id
left join tb_systemcategoryitem f on a.auditorid=f.id
where rownum < #{end}
 and a.idnumber like '%'||#{idNumber}||'%' 
 and a.realname=#{realName} 
 and a.auditorid=#{auditorId} 
) c where c.rn > #{start}

select count(*) from tb_realname where 1=1
 and idnumber like '%'||#{idNumber}||'%' 
 and realname=#{realName} 
 and auditorid=#{auditorId}
