mapper.select:
select * from ( select rownum rn,t.* from ( select a.id bid_id,a.bid_money,to_char(a.BEGINTIME,'yyyy-MM-dd') bid_creation_time, a.bid_minimum,d.item state,c.item cdate,to_char(a.BID_END_TIME,'yyyy-MM-dd') bid_end_time, a.userid,c.id from tb_tendering a join tb_systemcategoryitem c on a.bid_amortize=c.id join tb_systemcategoryitem d on a.stuts=d.id where a.stuts=21
and a.USERID=#{USERID}
and BID_AMORTIZE=#{BID_AMORTIZE} d order by bid_id DESC) t where rownum < #{end}) a where a.rn > #{start}

select count(*) from tb_tendering a join tb_systemcategoryitem c on a.bid_amortize=c.id join tb_systemcategoryitem d on a.bid_state=d.id where a.stuts=21
and a.USERID=#{USERID}
and BID_AMORTIZE=#{BID_AMORTIZE}
