mapper.select:
select * from
(select rownum rn,a.deptid,a.dutyid,a.empid,a.ename,a.sex,a.age,a.ephone,a.email,a.eident,a.birth,a.educ,to_char(a.hiredate,'yyyy-mm-dd') as hiredate,a.living,b.deptname,c.dutyname from tb_employee a
join tb_dept b on a.deptid=b.deptid
join tb_duty c on a.dutyid=c.dutyid
where rownum < #{end}


        and empid=#{empid}


        and ename like '%'||#{ename}||'%'


        and deptid=#{deptid}


) d where d.rn > #{start}

select count(*) from tb_employee


          and empid=#{empid}


          and ename like '%'||#{ename}||'%'


          and deptid=#{deptid}
