mapper.select:
       select a.money,b.item from tb_loanrecord a
join tb_systemcategoryitem b on a.longtime=b.id
 where tendid=#{tendId}

        select a.realid,((b.item/12)*0.20*a.money+a.money) mo from tb_loanrecord a
				join tb_systemcategoryitem b on a.longtime=b.id
				 where a.tendid=#{tendId}
